//在一个字符串(0<=字符串长度<=10000，全部由字母组成)中找到第一个只出现一次的字符,//并返回它的位置, 如果没有则返回 -1（需要区分大小写）class Solution {private:	struct location {		int appearCnt;		int index;        location() : appearCnt( 0 ), index( 0 ){}	};public:    int FirstNotRepeatingChar(string str) {        		if( str.size() <= 0 )			return -1;		map<char, location>mp;		for( int i = 0; i < str.size(); ++i ) {			++mp[ str[ i ] ].appearCnt;			mp[ str[ i ] ].index = i;		}		for( int i = 0; i < str.size(); ++i )			if( mp[ str[ i ] ].appearCnt == 1 )				return mp[ str[ i ] ].index;        return -1;    }};